import React, { Component } from 'react';
import { BrowserRouter, Route, Link } from 'react-router-dom';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import Chat from './Chat/Chat.js';
import Signin from './Pages/Signin.js';
import login from './Pages/login.js';
import modifyaccount from './Pages/modifyaccount.js';
import addcard from './Pages/addcard.js';
import modifycard from './Pages/modifycard.js';
import deletecard from './Pages/deletecard.js';
import balance from './Pages/balance.js';
import deposit from './Pages/deposit.js';
import withdrawal from './Pages/withdrawal.js';
import transfer from './Pages/transfer.js';

var users = [];

const user = {
  id: -1,
  first_name: 'string',
  last_name: 'string',
  email: 'email, string unique',
  password: 'string',
  is_admin: 'boolean'
};

class App extends Component {


  constructor(props) {
    super(props);
    this.state = { chat: [] };;
    this.handleSend = this.handleSend.bind(this);
  }

  allowedUser(user) {
    var found = users.find((name) => {
      return name === user;
    });
    console.log(found);
    return found;
  }

  handleSend(name, text) {
    this.setState({ chat: this.state.chat.concat(`${name}: ${text}`) });
  }

  render() {
    return (
      <BrowserRouter>
        <div>
          <li><Link to="/signin">Sign in</Link></li>
          <li><Link to="/login">Log in</Link></li>

          <li><Link to="/modifyaccount">Modify your account</Link></li>
          <li><Link to="/addcard">Add a card</Link></li>
          <li><Link to="/modifycard">Modify a card</Link></li>
          <li><Link to="/deletecard">Delete a card</Link></li>

          <li><Link to="/balance">Balance</Link></li>
          <li><Link to="/deposit">Deposit</Link></li>
          <li><Link to="/withdrawal">Withdrawal</Link></li>
          <li><Link to="/transfer">Transfers</Link></li>


          {Signin()}
          {login(this)}
          {modifyaccount()}
          {addcard()}
          {modifycard()}
          {deletecard()}
          {balance()}
          {deposit()}
          {withdrawal()}
          {transfer()}
          

        </div>
      </BrowserRouter>
    );
  }
}

export default App;
