import React, { Component } from 'react';

class AddCardForm extends Component {
    constructor(props) {
        super(props)
        console.log("props");
        console.log(props.output);
        this.state = { inputs: props.inputs };
        this.handleSendForm = this.handleSendForm.bind(this);
        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(event) {
        this.setState({ [event.target.name]: event.target.value });
    }

    handleSendForm() {
        this.props.onSend(this.state);
        this.setState({ inputs: '' });
    }

    displayInputs() {
        var inputs = "";
        alert(this.props.inputs);
        for (var i = 0; i < this.props.inputs; i++) {
            var inputText = this.props.text[i];
            var inputName = this.props.inputs[i];
            inputs += <p>{inputText} <input type="text" name={inputName} onChange={this.handleChange} value={this.state.inputs[inputName]} /></p>;
            alert(inputs);
        }
        /*
                let inputs = this.props.inputs.map((name) => {
                    return (<>
                        <p>{name} <input type="text" name={name} onChange={this.handleChange} value={this.state.inputs[name]} /></p>
                    </>);
                });
                */
            
        return (inputs);
    }

    render() {
        return (
            <div>
                {this.displayInputs()}
                <button onClick={this.handleSendForm}>{this.props.output}</button>
            </div>
        );
    }
}
export default AddCardForm
